import React, { useEffect, useState } from "react";
import SmallCard from './SmallCard';


function ContentRowTotales(props) {
    const [books, setBooks] = useState('');

    useEffect(() => {
        fetch('http://localhost:3030/API/books/')
            .then(res => res.json())
            .then(data => setBooks(data.meta.total))
            .catch(e => console.log(e))
    }, [])

    return (
        <React.Fragment>
            <div className="col-lg-6 mb-4">
                <div className="card shadow mb-4">
                    <div className="card-header py-3">
                        <h6 className="m-0 font-weight-bold text-gray-800">Total de libros</h6>
                    </div>
                    <div className="card-body card2">
                        <div className="row">
                            <div className="h5 mb-0 font-weight-bold text-gray-800">{books}</div>
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
}

export default ContentRowTotales;
