import React, { useEffect, useState } from "react";

function ContentRowTotales(props) {

    const [books, setBooks] = useState('');
    const [categories, setCategories] = useState('');
    const [users, setUsers] = useState('');

    useEffect(() => {
        fetch('http://localhost:3030/API/books/')
            .then(res => res.json())
            .then(data => setBooks(data.data.count))
            .catch(e => console.log(e))
    }, [])

    useEffect(() => {
        fetch('http://localhost:3030/API/users/list')
            .then(res => res.json())
            .then(data => setUsers(data.data.count))
            .catch(e => console.log(e))
    }, [])

    //  useEffect(() => {
    //  fetch('http://localhost:3001/api/users/list')
    //  .then(res => res.json())
    //  .then(data => setUsers(data.data.count))
    //   .catch(e => console.log(e))
    //  }, [])

    return (
        <React.Fragment>

            <div className="col-md-4 mb-4">
                <div>
                    <div className="card-body">
                        <div className="row no-gutters align-items-center">
                            <div className="col mr-2">
                                <div > Total de libros</div>
                                <div className="h5 mb-0 font-weight-bold text-gray-800">{books}</div>
                            </div>
                            <div className="col-auto">
                                <i className={`fas ${props.icon} fa-2x text-gray-300`}></i>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="col-md-4 mb-4">
                <div>
                    <div className="card-body">
                        <div className="row no-gutters align-items-center">
                            <div className="col mr-2">
                                <div > Total de usuarios</div>
                                <div className="h5 mb-0 font-weight-bold text-gray-800">{users}</div>
                            </div>
                            <div className="col-auto">
                                <i className={`fas ${props.icon} fa-2x text-gray-300`}></i>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </React.Fragment >
    )
}


export default ContentRowTotales;
