import React from 'react';
import BooksRow from './BooksRow';

let tableRowsData = [
    {
        Categorie: 'Drama',
        Length: '123',
        Books: ['Peli_1', 'Peli_2'],
    },
    {
        Categorie: 'Comedia',
        Length: '142',
        Books: ['Peli_1', 'Peli_2', 'Peli_3'],
    },

]
// NECESITO UN ARREGLO CON CATEGORIAS, Y QUE CADA UNA TENGA UN ARREGLO DE LIBROS DE ESA CATEGORÍA, Y SACAR ESE TOTAL

function Books() {
    return (
        /* <!-- DataTales Example --> */
        <div className="card shadow mb-4">
            <div className="card-body">
                <div className="table-responsive">
                    <table className="table table-bordered" id="dataTable" width="100%" cellSpacing="0">
                        <thead>
                            <tr>
                                <th>Título</th>
                                <th>Duración</th>
                                <th>Rating</th>
                                <th>Género</th>
                                <th>Premios</th>
                            </tr>
                        </thead>
                        <tfoot>
                            <tr>
                                <th>Título</th>
                                <th>Duración</th>
                                <th>Rating</th>
                                <th>Género</th>
                                <th>Premios</th>
                            </tr>
                        </tfoot>
                        <tbody>
                            {
                                tableRowsData.map((row, i) => {
                                    return <BooksRow {...row} key={i} />
                                })
                            }

                        </tbody>
                    </table>
                </div>
            </div>
        </div>

    )
}


export default Books;
