import React, { Component } from "react";
import SmallCard from './SmallCard';


class ContentRowTotales extends Component {
    constructor() {
        super();
        this.state = {
            books: []
        }
    }

    componentDidMount() {
        fetch('http://localhost:3030/API/books/')
            .then(res => res.json())
            .then(data => this.setState({ books: data.data.count }))
            .catch(e => console.log(e))
    }

    mouse() {
        const cardBody = document.querySelector('.card2');
        cardBody.classList.toggle('bg-secondary');
    };

    render() {
        return (
            <React.Fragment>
                <div className="col-lg-6 mb-4">
                    <div className="card shadow mb-4">
                        <div className="card-header py-3">
                            <h6 onMouseOver={this.mouse} className="m-0 font-weight-bold text-gray-800">Total de libros</h6>
                        </div>
                        <div className="card-body card2">
                            <div className="row">
                                {
                                    this.state.books.map((bookList, index) => {
                                        return <SmallCard  {...bookList} key={index} />
                                    })
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
export default ContentRowTotales;
