import React, { useEffect, useState } from "react";
import CategoriesRow from './CategoriesRow';

let tableRowsData = [
    {
        Categorie: 'Drama',
        Books: ['Libro_1', 'Libro_2'],
    },
    {
        Categorie: 'Comedia',
        Books: ['Libro_1', 'Libro_2', 'Libro_3'],
    },

]


// NECESITO UN ARREGLO CON CATEGORIAS, Y QUE CADA UNA TENGA UN ARREGLO DE LIBROS DE ESA CATEGORÍA, Y SACAR ESE TOTAL

function CategoriesInDb() {

    const [genres, setGenres] = useState('');

    useEffect(() => {
        fetch('http://localhost:3030/API/books/products')
            .then(res => res.json())
            .then(data => setGenres(data.data.booksByGenres))
            .catch(e => console.log(e))
    }, [])

    return (
        /* <!-- DataTales Example --> */
        <div className="card shadow mb-4">
            <div className="card-body">
                <div className="table-responsive">
                    <table className="table table-bordered" id="dataTable" width="100%" cellSpacing="0">
                        <thead>
                            <tr>
                                <th>Categorias</th>
                                <th>Total de libros</th>
                            </tr>
                        </thead>
                        <tbody>
                            {setGenres}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>

    )
}


export default CategoriesInDb;
